$(function() {
	var x ='';
	var pagos = {
		inicio: function () {
			pagos.GetTotal();
			pagos.AddClicks();
			pagos.TablaDetalle();
		},		
		GetTotal: function()
		{
			var total = 0;
			$('#tabla-detalle tr').find("td.money-values").each(function(indice, elemento) {

				total += parseInt($(elemento).text());
				$('#totalinc').text(total);

			}); 
		},
		AddClicks: function()
		{
			$('.boton-volver').on("click", function(){

				window.location.href = "pages/pagos/gestionar.php";

			});
		},TablaDetalle : function()
		{
			x = $('#tabla-detalle').DataTable({
				dom: 'Bfrtip',
				searching: true,
				paging: false,
				ordering: true,
				info:     true,
				buttons: [
				'csv', 'excel', 
				{
					extend: 'pdfHtml5',
					text: 'PDF',
					customize: function ( doc ) {
                // Splice the image in after the header, but before the table
                doc.content.splice( 1, 0, {
                	margin: [ 0, 0, 0, 12 ],
                	alignment: 'left',
                	image: 'data:image/png;base64,'
                } ),
                doc.content.splice( 2, 0, {
                	margin: [ 0, 0, 0, 12 ],
                	alignment: 'left',
                	text: [
                	{ text: $('.textodetalle').text(), fontSize: 15 },
                	''
                	]
                } ),doc.content.splice( 3, 0, {
                	margin: [ 0, 0, 0, 12 ],
                	alignment: 'left',
                	text: [
                	{ text: 'Fecha : ', fontSize: 13 },
                	$('.fechapago').val()
                	]
                } ),
                doc.content.splice( 4, 0, {
                	margin: [ 0, 0, 0, 12 ],
                	alignment: 'left',
                	text: [
                	{ text: 'Valor : ', fontSize: 13 },
                	$('.valorpago').val()
                	]
                } ),doc.content.splice( 5, 0, {
                	margin: [ 0, 0, 0, 12 ],
                	alignment: 'left',
                	text: [
                	{ text: 'Eps : ', fontSize: 13 },
                	$('.epspago').val()
                	]
                } )
                // Data URL generated by http://dataurl.net/#dataurlmaker
            }
        },
        {
        	extend: 'print',
        	text: 'Imprimir',
        	messageTop: 'The information in this table is copyright to Sirius Cybernetics Corp.'
        }
        ],
        order: [[ 2, "desc" ]]

    });

		}
	};
	$(document).ready(function () {

		pagos.inicio();

	});

});